import React from "react";
import './Basket.css';
import tomato from './tomato.jpg';
import { Link } from "react-router-dom";

function Basket({ cart }) {
  // Calculate the overall total price
  const overallTotal = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <div className="basket">
      {cart.length === 0 ? (
        <div className="center">
          <h3>Oops! Your basket is empty!</h3>
          <Link className="link" to="/shop">
            <button className="shopnow">Shop now</button>
          </Link>
        </div>
      ) : (
        <div>
          <h2>Your Basket</h2>
          <ul className="basket-list">
            {cart.map((item, index) => (
              <li key={index} className="basket-item">
                <div className="basket-card">
                  {/* Render the "Card" layout for each item in the basket */}
                  <div className="image">
                    <img src={item.vegimage} alt={item.vegname} />
                  </div>
                  <div className="info">
                    <h3>{item.vegname}</h3>
                    <p>Location: {item.location}</p>
                    <p>Quantity: {item.quantity} kg</p>
                    <p>Price: ₹{item.price}/kg</p>
                    <p>Total: ₹{(item.price * item.quantity).toFixed(2)}</p>
                  </div>
                </div>
              </li>
            ))}
          </ul>
          <h3 className="overall-total">Overall Total: ₹{overallTotal.toFixed(2)}</h3>
        </div>
      )}
    </div>
  );
}

export default Basket;
