import React, { useState } from "react";
import "./Navbar.css";
import { Link } from "react-router-dom";

function Navbar({ cart }) {
  const [isOpen, setIsOpen] = useState(false); // State for toggling menu

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className="nav">
      <p>WorldPeas</p>
      <div className="hamburger" onClick={toggleMenu}>
        {/* Hamburger Icon */}
        <div className="bar"></div>
        <div className="bar"></div>
        <div className="bar"></div>
      </div>
      <div className={`Rightsection ${isOpen ? "show" : ""}`}>
        <Link className="link" to="/home" onClick={() => setIsOpen(false)}>Home</Link>
        <Link className="link" to="/whoweare" onClick={() => setIsOpen(false)}>Who we are</Link>
        <Link className="link" to="/shop" onClick={() => setIsOpen(false)}>Shop</Link>
        <Link className="link" to="/myaccount" onClick={() => setIsOpen(false)}>Myaccount</Link>
        <Link className="link" to="/basket" onClick={() => setIsOpen(false)}>
          <button className="btn">Basket ({cart.length})</button>
        </Link>
      </div>
    </div>
  );
}

export default Navbar;
