import React from "react";
import Card from "./components/Card";
import tomato from './tomato.jpg'
import ginger from './ginger.jpg'
import onion from './onion.jpg'
import { useState } from "react";
import './Shop.css'


function Shop(){
    const [displayStyle, setDisplayStyle] = useState('flex');

    const Shop = ({ addToBasket }) => {
        // List of items (vegetables) available in the shop
        const items = [
          { id: 1, vegname:'Heirloom tomato', price: 100, location: 'Grown in San Juan Capistrano, CA', className: 'tomato' },
          { id: 2, vegname: 'Organic Ginger', price: 150, location: 'Grown in Huntington beach, CA', className: 'ginger' },
          { id: 3, vegname: 'Organic Onion', price: 50, location: 'Grown in hersberg, Italy', className: 'onion' },
        ];
    }
    

    return(
        <div className="shop">
            <div className="shop_header">
                <div className="header_left">
                    <p className="produce">Produce</p>
                    <p className="fresh">Fresh - </p>
                    <p className="date">&nbsp;October 9, 2024</p>
                </div>
                <div className="header_right">
                    <button className="default" onClick={() => setDisplayStyle('flex')}>Default</button>
                    <button className="listview" onClick={() => setDisplayStyle('block')}>List view</button>
                </div>
            </div>
            <div className="line"></div>
            <div className="shop-container">
                {items.map((item) => (
                    <div className="cards" style={{ display: displayStyle, flexDirection: displayStyle === 'flex' ? 'row' : 'column' }}>
                    <Card 
                      vegimage={tomato} 
                      vegname="Heirloom tomato" 
                      price="$5.99 / lb" 
                      location="Grown in San Juan Capistrano, CA"
                    />
                    <Card 
                      vegimage={ginger} 
                      vegname="Organic ginger" 
                      price="$12.99 / lb" 
                      location="Grown in Huntington beach, CA"
                    />
                    <Card 
                      vegimage={onion} 
                      vegname="Organic onion" 
                      price="$10.99 / lb" 
                      location="Grown in hersberg, Italy"
                    />
                    </div>
                ))}
            </div>
        </div>
    )
}

export default Shop
