import React from "react";
import Card from "./components/Card";
import tomato from './tomato.jpg'
import ginger from './ginger.jpg'
import onion from './onion.jpg'
import { useState } from "react";
import './Shop.css'

function Shop(){
    const [displayStyle, setDisplayStyle] = useState('block');
    const [activeButton, setActiveButton] = useState(null);
    const handleButtonClick = (button) => {
        setActiveButton(button);
      };

    return(
        <div className="shop">
            <div className="shop_header">
                <div className="header_left">
                    <p className="produce">Produce</p>
                    <p className="fresh">Fresh -</p>
                    <p className="date">&nbsp;October 9, 2024</p>
                </div>
                <div className="header_right">
                    <button className="default" onClick={() => {setDisplayStyle('flex'); handleButtonClick;style={
                        backgroundColor: activeButton === 1 ? 'olivedrab' : 'default',
                        }}}>Default</button>
                    <button className="listview" onClick={() => setDisplayStyle('block')}>List view</button>
                </div>
            </div>
            <div className="line"></div>
            <div className="cards" style={{ display: displayStyle, flexDirection: displayStyle === 'flex' ? 'row' : 'column' }}>
            <Card vegimage={tomato} vegname="Heirloom tomato" price="$5.99 / lb" location="Grown in San Juan Capistrano, CA"/>
            <Card vegimage={ginger} vegname="Organic ginger" price="$12.99 / lb" location="Grown in Huntington beach, CA"/>
            <Card vegimage={onion} vegname="Organic onion" price="$10.99 / lb" location="Grown in hersberg, Italy"/>
            </div>
        </div>
    )
}
export default Shop
